bscNode = "/root/node/geth.ipc"
web3 = Web3(Web3.IPCProvider(bscNode))
web3.middleware_onion.inject(geth_poa_middleware, layer=0)

def handle_event(event):
    print(event.decode())

def isBNBLiquidity(tokenAddress):
    pairAddress = getPair(tokenAddress)
    pairReserves, token0, token1 = getReserves(pairAddress)
    BNBReserve = 0
    TokenReserve = 0

    if token0 == WBNBAdress or token1 == WBNBAdress:
        return True
    else:
        return False

def log_loop(event_filter, poll_interval):
    while True:
        try:
            block_hashes = web3.eth.get_filter_changes(event_filter.filter_id)
        except:
            pass
        for block_hash in block_hashes:
            block = web3.eth.get_block(block_hash.hex())
            transactions = block['transactions']
            try:
                for tx in transactions:
                    tx_buff = web3.eth.get_transaction(tx)
                    walletAddress = tx_buff['from']
                    if walletAddress.lower() in WALLETS_TO_MONITOR:
                        contract = web3.eth.contract(address=pancakeSwapRouterAddress, abi=pancakeABI)
                        try:
                            func_obj, func_params = contract.decode_function_input(tx_buff['input'])
                        except:
                            pass
                        else:
                            tokenAddress = web3.toChecksumAddress(func_params['path'][-1])
                            if isBNBLiquidity(tokenAddress):
                                gasPrice = str(int(tx_buff['gasPrice'])/math.pow(10, 9))
                                now = datetime.now()
                                current_time = now.strftime("%H:%M:%S")
                                print("---------- FOUND ----------")
                                print('tokenAddress : ', tokenAddress)
                                print('walletAddress : ', walletAddress)
                                print('gasPrice : ', gasPrice)
                                print("Time : ", current_time)
                                print("---------------------------")
                                print("\n \n")
            except Exception as e:
                pass
                # print("e : " + str(e))
                
        time.sleep(poll_interval)

def main():
    block_filter = web3.eth.filter('latest')
    log_loop(block_filter, 0.4)

if __name__ == '__main__':
    main()

